import React, { useEffect, useState } from 'react';
import { getlocalData, removelocalData, isSessionSet } from './session';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap-icons/font/bootstrap-icons.css';
import './../css/utilities.css';

const Sidebar = ({ children }) => {
  const [search, setSearch] = useState('');

  const logoutHandler = (e) => {
    localStorage.clear()
  }

  const handleSearch = (e) => {
    e.preventDefault();
    setSearch(e.target.value);
  }

  const goSearch =  (e) => {
    e.preventDefault();
    window.location.href = '/search?search=' + search;
  }

  if(isSessionSet('session') && isSessionSet('isLoggedIn')) {
      const expDate = getlocalData('expDate');
      if (Date.now() >= expDate){
        logoutHandler();
        window.location.href = '/token-expired';
      } else {
      var session = getlocalData('session');
      var isLoggedIn = getlocalData('isLoggedIn');
      }
  }

  return (
    <div className='container-fluid'>
      <div className='row'>
        <div className='bg-dark col-auto col-md-2 min-vh-100 d-flex justify-content-betweet flex-column'>
          <a className='text-decoration-none text-white d-flex align-itemcenter ms-3 mt-2' href='/'>
            <span className='ml-1 fs-4'>CS HUB</span>
          </a>
          <hr className='text-secondary'/>
          <ul className='nav nav-pills flex-column'>
            <li className='nav-item text-white fs-4 my-1'>
              <a href='/' className='nav-link text-white fs-5' aria-current="page">
                <i className="bi bi-house"></i>
                <span className='ms-2'>Home</span>
              </a>
            </li>
            <li className='nav-item text-white fs-4 my-1'>
              <a href='/' className='nav-link text-white fs-5' aria-current="page">
                <i class="bi bi-person"></i>
                <span className='ms-2'>Profile</span>
              </a>
            </li>
            <li className='nav-item text-white fs-4 my-1'>
              <a href='/' className='nav-link text-white fs-5' aria-current="page">
                <i class="bi bi-clock-history"></i>
                <span className='ms-2'>History</span>
              </a>
            </li>
            <li className='nav-item text-white fs-4 my-1'>
              <a href='/' className='nav-link text-white fs-5' aria-current="page">
                <i class="bi bi-upload"></i>
                <span className='ms-2'>Upload Video</span>
              </a>
            </li>
            <li className='nav-item text-white fs-4 my-1'>
              <a href='/' className='nav-link text-white fs-5' aria-current="page">
                <i class="bi bi-gear"></i>
                <span className='ms-2'>Administration</span>
              </a>
            </li>
            <li className='nav-item text-white fs-4 my-1'>
              <a href='/' className='nav-link text-white fs-5' aria-current="page">
                <i class="bi bi-box-arrow-right"></i>
                <span className='ms-2'>Logout</span>
              </a>
            </li>
          </ul>
        </div>
        <div class='dropdown open'>
          <a className='btn btn-secondary dropdown-toggle' href='#' role='button' id='dropdownMenuLink' data-bs-toggle='dropdown' aria-expanded='false'>
            <i class="bi bi-person-circle"></i> Tor
          </a>
        </div>
      </div>
    </div>




  // return (
    
  //   <div className="d-flex" style={{ minHeight: '100vh' }} id={'sideBar'}>
  //     <div className="position-fixed h-100" style={{ width: '250px', backgroundColor: 'rgb(44,48,52)' }}>
