  //                   <button className="btn btn-light rounded-pill" style={{ width: '230px', color: 'black' }}>Login</button>
  //                 </a>
  //                 </>
  //               )}
  //             </nav>
  //           </div>
  //         </div>
  //       </div>
  //     </div>
  //     {/* end of side bar */}
